import React, { useState } from 'react';
import { Text, View, StyleSheet, Image, Button, Alert} from 'react-native';


export default function Previsions() {

    // const [city, setCity] = useState('');
    // const [desc1, setDesc] = useState('');
        // const [min1, setMin1] = useState('');
    const [icon1, setIcon1] = useState('');
    const [date1, setDate1] = useState('');
    const [max1, setTempMax1] = useState('');
    const [icon2, setIcon2] = useState('');
    const [date2, setDate2] = useState('');
    const [max2, setTempMax2] = useState('');
    // const [country, setCountry] = useState('F');

   fetch(`http://api.openweathermap.org/data/2.5/forecast?q=Rieux&appid=f89a907521cb0fb9b77799ba225f55ce&units=metric&lang=fr`)
      .then((response) => response.json())
      .then(r => { 
        //  setCity(r.list) 
        //  setDesc1(r.list[0].description)
         setIcon1(r.list[0].weather[0].icon)
         setDate1(r.list[0].dt_txt)
         setTempMax1(r.list[0].main.temp_max)
         setIcon2(r.list[1].weather[0].icon)
         setDate2(r.list[1].dt_txt)
         setTempMax2(r.list[1].main.temp_max)
      })


    return (
            <View style={styles.content}>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={{
                    uri: `http://openweathermap.org/img/wn/${icon1}@2x.png`,
                }} />
                    <Text style={styles.p}>{date1}</Text>
                    <Text style={styles.p}>{max1}°C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={{
                    uri: `http://openweathermap.org/img/wn/${icon2}@2x.png`,
                }} />
                    <Text style={styles.p}>{date2}</Text>
                    <Text style={styles.p}>30°C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={require('../assets/01d.png')} />
                    <Text style={styles.p}>Dimanche</Text>
                    <Text style={styles.p}>30°C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={require('../assets/01d.png')} />
                    <Text style={styles.p}>Lundi</Text>
                    <Text style={styles.p}>30°C</Text>
                </View>
            </View>
    );
}

const styles = StyleSheet.create({
    content: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
      width: '100%'
    },

    h1: {
        fontSize: 18,
        marginTop: 40,
        color: 'white'
    },

    h2: {
        marginTop: 10,
        fontSize: 25,
        color: 'white'
    },

    p: {
        fontSize: 12,
        margin: 5,
        color: 'white'
    },

    weatherCol: {
        marginTop: 20,
        display: 'flex',
        width: 80,
        padding: 5,
        flexDirection: 'column',
        alignItems: 'center',
        borderStyle: 'solid',
        borderWidth: 1,
        borderLeftColor: 'white',
        borderRightColor: 'white',
        borderTopWidth: 0,
        borderBottomWidth: 0
    },

    iconWeather: {
        height: 30,
        width: 30,
    },


  });
