import React from 'react';
import { Text, View, StyleSheet, Image, Button, Alert } from 'react-native';

export default function Home() {
    return (
            <View style={styles.content}>
                <Text style={styles.h1}>Prévisions pour aujourd'hui à</Text>
                <Text style={styles.h2}>Paris (75)</Text>
                <View style={styles.weather}>
                    <Image style={styles.iconWeather} source={require('../assets/sun.png')} />
                        <View>
                            <Text style={styles.p}>30°C</Text>
                            <Text style={styles.p}>Ciel dégagé</Text>
                            <Text style={styles.p}>30/03/2042 10:41</Text>
                        </View>
                </View>
                <Button
        style={{fontSize: 20, color: 'green'}}
        styleDisabled={{color: 'red'}}
        onPress={() => this._handlePress()}>
        Press Me!
      </Button>
                <Button style={styles.prevButton} title="Voir plus" onPress={() => Alert.alert('Coucou')}/>
            </View>
    );
}

const styles = StyleSheet.create({
    content: {
    display: 'flex',
    alignItems: 'center',
      height: '100%',
      width: '100%'
    },

    h1: {
        fontSize: 18,
        marginTop: 40,
        color: 'black'
    },

    h2: {
        marginTop: 10,
        fontSize: 25,
        color: 'black'
    },

    p: {
        fontSize: 18,
        margin: 5,
        color: 'black'
    },

    weather: {
        marginTop: 20,
        display: 'flex',
        padding: 20,
        flexDirection: 'row',
        borderStyle: 'solid',
        borderWidth: 4,
        borderRadius: 5,
        borderColor: 'white' 
    },

    iconWeather: {
        height: 100,
        width: 100,
        marginRight: 20
    },

    prevButton: {
        color: 'black',
        width: 100,
        height: 100
    }

  });
