import React, { useState } from 'react';
import { Text, View, StyleSheet, Image, Button, Alert} from 'react-native';


export default function Previsions() {

    const [city, setCity] = useState('');
    const [desc1, setDesc] = useState('');
    const [icon1, setIcon1] = useState('');
    const [temp1, setTemp1] = useState('');
    const [min1, setMin1] = useState('');
    const [max1, setMax1] = useState('');
    // const [country, setCountry] = useState('F');

   fetch(`https://api.openweathermap.org/data/2.5/forecast?q=Rieux&appid=f89a907521cb0fb9b77799ba225f55ce&lang=fr`)
      .then((response) => response.json())
      .then(r => { 
         setCity(r.list) 
         setDesc1(r.list[0].description)
         setIcon1(r.weather[0].icon)
         setTemp1(r.main.temp.toFixed(1))
         setMin1(r.main.temp_min.toFixed(1))
         setMax1(r.main.temp_max.toFixed(1))

      })


    return (
            <View style={styles.content}>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={{
                    uri: `http://openweathermap.org/img/wn/${icon}@2x.png`,
                }} />
                    <Text style={styles.p}>Vendredi</Text>
                    <Text style={styles.p}>30째C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={require('../assets/01d.png')} />
                    <Text style={styles.p}>Samedi</Text>
                    <Text style={styles.p}>30째C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={require('../assets/01d.png')} />
                    <Text style={styles.p}>Dimanche</Text>
                    <Text style={styles.p}>30째C</Text>
                </View>
                <View style={styles.weatherCol}>
                    <Image style={styles.iconWeather} source={require('../assets/01d.png')} />
                    <Text style={styles.p}>Lundi</Text>
                    <Text style={styles.p}>30째C</Text>
                </View>
            </View>
    );
}

const styles = StyleSheet.create({
    content: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
      width: '100%'
    },

    h1: {
        fontSize: 18,
        marginTop: 40,
        color: 'white'
    },

    h2: {
        marginTop: 10,
        fontSize: 25,
        color: 'white'
    },

    p: {
        fontSize: 12,
        margin: 5,
        color: 'white'
    },

    weatherCol: {
        marginTop: 20,
        display: 'flex',
        width: 80,
        padding: 5,
        flexDirection: 'column',
        alignItems: 'center',
        borderStyle: 'solid',
        borderWidth: 1,
        borderLeftColor: 'white',
        borderRightColor: 'white',
        borderTopWidth: 0,
        borderBottomWidth: 0
    },

    iconWeather: {
        height: 30,
        width: 30,
    },


  });
