import { Text, View, StyleSheet, Image} from 'react-native';
import fetchJsonp from 'fetch-jsonp';


fetchJsonp(`http://api.openweathermap.org/data/2.5/weather?q=Rieux&appid=f89a907521cb0fb9b77799ba225f55ce&units=metric&lang=fr`)
.then(res => res.json())
.then(weather => weather.description)
.then(name => {
setMusics(name);
});


export default function Home() {
    console.log(fetchJsonp(`http://api.openweathermap.org/data/2.5/weather?q=Rieux&appid=f89a907521cb0fb9b77799ba225f55ce&units=metric&lang=fr`));
    return (
            <View style={styles.content}>
                <Text style={styles.h1}>Prévisions pour aujourd'hui à</Text>
                <Text style={styles.h2}>Paris (75)</Text>
                <View style={styles.weather}>
                    <Image style={styles.iconWeather} source={require('../assets/sun.png')} />
                        <View>
                            <Text style={styles.p}>30°C</Text>
                            <Text style={styles.p}>Ciel dégagé</Text>
                            <Text style={styles.p}>30/03/2042 10:41</Text>
                        </View>
                </View>
            </View>
    );
}

const styles = StyleSheet.create({
    content: {
    display: 'flex',
    alignItems: 'center',
      width: '100%'
    },

    h1: {
        fontSize: 18,
